prompt --application/shared_components/security/authentications/db_login
begin
wwv_flow_imp_shared.create_authentication(
 p_id=>wwv_flow_imp.id(95938415727122258727)
,p_name=>'DB Login'
,p_scheme_type=>'NATIVE_CUSTOM'
,p_attribute_03=>'validate_user_DB'
,p_attribute_05=>'N'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION validate_user_DB (p_username IN VARCHAR2, p_password IN VARCHAR2)',
'RETURN BOOLEAN',
'AS',
'user_role varchar2(20);',
'cursor login_cur is',
'select ''USER''',
'from PARTICIPANTS',
'where upper(EMAIL)= upper(:p_username) and PHONE_NUMBER = :p_password',
'UNION',
'select ''STAFF''',
'from STAFF',
'where upper(EMAIL)= upper(:p_username) and PHONE_NUMBER = :p_password and STAFF_ID != 1',
'UNION',
'select ''MANAGER''',
'from STAFF',
'where upper(EMAIL)= upper(:p_username) and PHONE_NUMBER = :p_password and STAFF_ID = 1;',
'begin',
'open login_cur;',
'fetch login_cur into user_role;',
'if login_cur%found then',
'--apex_util.set_session_state(''APP_USER'', p_username);',
'apex_util.set_session_state(''user_role'', user_role);',
'return true;',
'end if;',
'return false;',
'end;',
