,p_plug_display_sequence=>10
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    e.event_name,',
'    e.event_type,',
'',
'    -- Total Registered',
'    COUNT(p.participant_id) AS Total_Registered,',
'',
'    -- Total Attending (Paid)',
'    COUNT(CASE WHEN p.payment_status = ''Paid'' THEN 1 END) AS Total_Attending,',
'',
'    -- Attendance Percentage',
'    NVL(',
'        ROUND(',
'            (COUNT(CASE WHEN p.payment_status = ''Paid'' THEN 1 END) /',
'            NULLIF(COUNT(p.participant_id), 0)) * 100.0, 2',
'        ), 0',
'    ) || ''%'' AS Attendance_Percentage,',
'',
'    -- Attendee Category (VIP, Speaker, Regular)',
'    COUNT(CASE WHEN p.category = ''VIPs'' THEN 1 END) AS VIP_Attendees,',
'    COUNT(CASE WHEN p.category = ''Speaker'' THEN 1 END) AS Speaker_Attendees,',
'    COUNT(CASE WHEN p.category = ''Regular Guests'' THEN 1 END) AS Regular_Attendees,',
'',
'    -- Total Revenue (sum of all paid payments)',
'    NVL(SUM(CASE WHEN p.payment_status = ''Paid'' THEN pay.amount END), 0) AS Revenue,',
'',
'    -- Venue Cost',
'    NVL(e.venue_cost, 0) AS Venue_Cost,',
'',
'    -- Service Costs',
'    NVL(SUM(t.cost), 0) AS Service_Cost,',
'',
'    -- Total Costs (Venue + Service)',
'    NVL(e.venue_cost, 0) + NVL(SUM(t.cost), 0) AS Total_Costs,',
'',
'    -- Profit (Revenue - Total Costs)',
'    NVL(SUM(CASE WHEN p.payment_status = ''Paid'' THEN pay.amount END), 0) - ',
'    (NVL(e.venue_cost, 0) + NVL(SUM(t.cost), 0)) AS Profit',
'',
'FROM Events e',
'LEFT JOIN Participants p ON p.event_id = e.event_id',
'LEFT JOIN Payments pay ON pay.participant_id = p.participant_id',
'LEFT JOIN Tasks t ON t.event_id = e.event_id',
'',
'GROUP BY e.event_name, e.event_type, e.venue_cost;',
''))
